import React, { useState, useRef } from 'react';

function MyComponent() {
    const [name, setName] = useState('Arbaaz Chouhan');
    const inputRef = useRef("");

    const handleClick = () => {
        setName('');
        inputRef.current.focus();
    };

    return (
        <div>
            <input ref={inputRef} type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Enter your name" />
            <button onClick={handleClick}>Restart</button>
        </div>
    );
}

export default MyComponent;
